insert into `article` (`id`, `title`, `content`, `description`, `keywords`, `create_date`, `update_date`) values('1264','二甲酒（内痔）','[配方] 穿山甲(炮)30克，人指甲(炒)5克，三花酒适量。<br />[制法] 将上药共研细末，备用。<br />[功用] 活血通络止痛。<br />[主治] 内痔。<br />[用法] 口服。每次取药未1―1．5克，用三花酒10-15毫升送服， 日服2次，连服5-8天。<br />[附记] 引自《医学文选、祖传秘方验方集》。方名为编者拟加','[配方] 穿山甲(炮)30克，人指甲(炒)5克，三花酒适量。[制法] 将上药共研细末，备用。[功用] 活血通络止痛。[主治] 内痔。[用法] 口服。每次取药未1―1．5克，用三花酒10-15毫升送服，','二甲酒（内痔）,药酒配方,民间药酒偏方,民间药酒配方大全,民间药酒偏方大全','1292319850','1292319850');
insert into `article` (`id`, `title`, `content`, `description`, `keywords`, `create_date`, `update_date`) values('1265','觅根酒（跌打损伤）','[配方] 觅根30―90克．白酒500毫升。<br />[制法] 将上药洗净，切碎，置容器中加入白酒、密封，浸泡10天后，过滤去渣．即成。<br />[功用] 舒筋活络、活血止血。<br /> [主治] 跌打损伤、阴囊肿痛、痔疮、牙痛等症。<br />[用法] 口服，每次服lO-15毫升，日服2次。<br />[附记] 引自《民间百病良方》。<br />','[配方] 觅根30―90克．白酒500毫升。[制法] 将上药洗净，切碎，置容器中加入白酒、密封，浸泡10天后，过滤去渣．即成。[功用] 舒筋活络、活血止血。 [主治] 跌打损伤、阴囊肿痛、痔疮、牙痛等','觅根酒（跌打损伤）,药酒配方,民间药酒偏方,民间药酒配方大全,民间药酒偏方大全','1292319852','1292319852');
